import React, { useState, useEffect } from 'react';
import { Loader } from './Loader';
import Pagination from './Pagination';

interface TUser {
  id: string;
  name: string;
  balance: number;
  email: string;
  registerAt: string;
  active: boolean;
}

const UserTable: React.FC = () => {
  const [users, setUsers] = useState<TUser[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [page, setPage] = useState<number>(1);
  const [rowsPerPage, setRowsPerPage] = useState<number>(10);
  const [totalPages, setTotalPages] = useState<number>(0);

  useEffect(() => {
    fetchUsers();
  }, [page, rowsPerPage]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      // Fetch data from the public JSON file
      const response = await fetch('/users.json');
      const data = await response.json();
      setUsers(data);
      setTotalPages(Math.ceil(data.length / rowsPerPage));
    } catch (error) {
      console.error('Error fetching users:', error);
    }
    setLoading(false);
  };

  return (
    <div>
      {loading ? <Loader /> : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Balance ($)</th>
              <th>Email</th>
              <th>Registration</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.slice((page - 1) * rowsPerPage, page * rowsPerPage).map(user => (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.balance.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}</td>
                <td><a href={`mailto:${user.email}`}>{user.email}</a></td>
                <td title={user.registerAt}>{new Date(user.registerAt).toLocaleDateString()}</td>
                <td>{user.active ? 'Active' : 'Inactive'}</td>
                <td>
                  <button>Edit</button>
                  <button>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <Pagination page={page} totalPages={totalPages} setPage={setPage} />
    </div>
  );
};

export default UserTable;
