import React, { useState, useEffect } from 'react';

interface TUser {
  id: string;
  name: string;
  balance: number;
  email: string;
  registerAt: string;
  active: boolean;
}

const UserTable: React.FC = () => {
  const [users, setUsers] = useState<TUser[]>([]);
  const [page, setPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    // Fetch user data (you can replace this with an API call later)
    fetchUsers();
  }, [page, rowsPerPage]);

  const fetchUsers = () => {
    // Replace with real API call
    const sampleData = [
      { id: '1', name: 'Andrew Taylor', balance: 2659, email: 'nam@mail.com', registerAt: '2020-09-23', active: true },
      { id: '2', name: 'Alvaro Garcia', balance: 2153, email: 'btaylor@yahoo.com', registerAt: '2022-01-31', active: false },
      // ... more users
    ];
    setUsers(sampleData);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Balance ($)</th>
            <th>Email</th>
            <th>Registration</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.slice((page - 1) * rowsPerPage, page * rowsPerPage).map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.balance.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}</td>
              <td><a href={`mailto:${user.email}`}>{user.email}</a></td>
              <td title={user.registerAt}>{user.registerAt}</td>
              <td>{user.active ? 'Active' : 'Inactive'}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
        <button onClick={() => setPage(page + 1)} disabled={page * rowsPerPage >= users.length}>Next</button>
      </div>
    </div>
  );
};

export default UserTable;
