import React, { useState, useEffect } from "react";
import { Loader } from "./Loader";
import Pagination from "./Pagination";

interface TUser {
  id: string;
  name: string;
  balance: number;
  email: string;
  registerAt: string;
  active: boolean;
}

const UserTable: React.FC = () => {
  const [users, setUsers] = useState<TUser[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [page, setPage] = useState<number>(1);
  const [rowsPerPage, setRowsPerPage] = useState<number>(10);
  const [totalPages, setTotalPages] = useState<number>(0);

  // Để quản lý trạng thái modal chỉnh sửa
  const [isEditModalOpen, setIsEditModalOpen] = useState<boolean>(false);
  const [currentUser, setCurrentUser] = useState<TUser | null>(null);

  useEffect(() => {
    fetchUsers();
  }, [page, rowsPerPage]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await fetch("/users.json");
      const data = await response.json();
      setUsers(data);
      setTotalPages(Math.ceil(data.length / rowsPerPage));
    } catch (error) {
      console.error("Lỗi khi lấy dữ liệu người dùng:", error);
    }
    setLoading(false);
  };

  // Hàm xử lý chỉnh sửa người dùng
  const handleEdit = (user: TUser) => {
    setCurrentUser(user);
    setIsEditModalOpen(true); // Mở modal chỉnh sửa
  };

  // Hàm xử lý xóa người dùng
  const handleDelete = (userId: string) => {
    const updatedUsers = users.filter((user) => user.id !== userId);
    setUsers(updatedUsers);
  };

  // Hàm lưu thay đổi sau khi chỉnh sửa
  const handleSaveEdit = () => {
    if (currentUser) {
      const updatedUsers = users.map((user) =>
        user.id === currentUser.id ? currentUser : user
      );
      setUsers(updatedUsers);
      setIsEditModalOpen(false); // Đóng modal sau khi lưu
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    if (currentUser) {
      const { name, value } = e.target;
      // Nếu name là "active", chuyển "active" thành true, "inactive" thành false
      setCurrentUser({
        ...currentUser,
        [name]: name === "active" ? value === "active" : value,  // true/false chuyển đổi
      });
    }
  };


  return (
    <div>
      {loading ? (
        <Loader />
      ) : (
        <div>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Balance ($)</th>
                <th>Email</th>
                <th>Registration</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {users
                .slice((page - 1) * rowsPerPage, page * rowsPerPage)
                .map((user) => (
                  <tr key={user.id}>
                    <td>{user.name}</td>
                    <td>
                      {user.balance.toLocaleString("en-US", {
                        style: "currency",
                        currency: "USD",
                      })}
                    </td>
                    <td>
                      <a href={`mailto:${user.email}`}>{user.email}</a>
                    </td>
                    <td title={user.registerAt}>
                      {new Date(user.registerAt).toLocaleDateString()}
                    </td>
                    <td>{user.active ? "Active" : "Inactive"}</td>
                    <td>
                      <button onClick={() => handleEdit(user)}>Edit</button>
                      <button onClick={() => handleDelete(user.id)}>
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>

          <Pagination page={page} totalPages={totalPages} setPage={setPage} />
        </div>
      )}

      {/* Modal Edit */}
      {isEditModalOpen && currentUser && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit User</h2>
            <form>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={currentUser.name}
                  onChange={handleChange}
                />
              </label>
              <label>
                Balance:
                <input
                  type="number"
                  name="balance"
                  value={currentUser.balance}
                  onChange={handleChange}
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={currentUser.email}
                  onChange={handleChange}
                />
              </label>
              <label>
                Registration:
                <input
                  type="text"
                  name="registerAt"
                  value={new Date(currentUser.registerAt).toLocaleDateString()}
                  disabled
                />
              </label>
              <label>
                Status:
                <select
                  name="active"
                  value={currentUser.active ? "active" : "inactive"} // Chuyển true/false thành "active"/"inactive"
                  onChange={(e) =>
                    handleChange({
                      target: {
                        name: "active",
                        value: e.target.value === "active", // Chuyển "active" thành true và "inactive" thành false
                      },
                    })
                  }
                >
                  <option value="active">Active</option>
                  <option value="inactive">Inactive</option>
                </select>
              </label>
              <button type="button" onClick={handleSaveEdit}>
                Save
              </button>
              <button type="button" onClick={() => setIsEditModalOpen(false)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserTable;
