import React, { useState } from "react";
import { TUser, usersData } from "../data";
import { useTable, Column } from "react-table";

const UsersTable = () => {
  const [page, setPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // Xử lý phân trang
  const startIndex = (page - 1) * rowsPerPage;
  const endIndex = startIndex + rowsPerPage;
  const usersToShow = usersData.slice(startIndex, endIndex);

  // Định nghĩa các cột trong bảng và khai báo kiểu Column<TUser>
  const columns: Column<TUser>[] = React.useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Balance",
        accessor: "balance",
      },
      {
        Header: "Email",
        accessor: "email",
      },
      {
        Header: "Registration Date",
        accessor: "registerAt",
        Cell: ({ value }: any) => {
          return new Date(value).toLocaleDateString(); // Hiển thị ngày dưới dạng dd/mm/yyyy
        },
      },
      {
        Header: "Active",
        accessor: "active", // Truy cập trường active trong dữ liệu
        Cell: ({ value }: any) => (value ? "Active" : "Inactive"), // Hiển thị "Active" hoặc "Inactive"
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data: usersToShow,
    });

  return (
    <div>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Phân trang */}
      <div>
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>
          Previous
        </button>
        <button
          onClick={() => setPage(page + 1)}
          disabled={page * rowsPerPage >= usersData.length}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default UsersTable;
